mapper.insert:
INSERT INTO DApply (
	dcharId, dgroupId
) VALUES (
	#{dcharId}, #{dgroupId}
)

mapper.select:
SELECT lapplyId, dcharId, dgroupId, lapplyResult
FROM DApply
WHERE dcharId = #{dcharId} AND dgroupId = #{dgroupId}

mapper.update:
UPDATE DApply
SET lapplyResult = #{lapplyResult}
WHERE lapplyId = #{lapplyId} AND dgroupId = #{dgroupId}
